(function($, exports, undefined){
    window.addEventListener('message', function(e){
      if(e.origin !== 'https://bostinno.streetwise.co')
        return;
      var split = e.data.split('|');
      if(split[0] == 'reload')
        return window.location.reload();
      if(split[0] == 'authenticationError')
        return Streetwise.ui.showAuthenticationError(split[1]);
      $('.js-login-signup-iframe-target').removeClass(function(i, css){
        return (css.match(/\bdropdown-iframe-class--\S+/g) || []).join(' ');
      });
      if(split[1] == 'remove'){
        $('.js-login-signup-iframe-target').addClass('hidden');
        $('.js-login-locker-ajax-wrapper').removeClass('hidden');
        $('.js-login-signup-iframe-target').closest('.modal--login-locker').removeClass('register-locker');
        $('.modal--login-locker').removeClass(split[2]);
        $('.js-login-signup-iframe-target').on('load', function(e){
          $('.js-login-signup-iframe-target').removeClass('hidden');
          $('.js-login-locker-ajax-wrapper').addClass('hidden');
        });
        return;
      }
      if(split[1] == 'register-locker'){
        $('.js-login-locker-ajax-wrapper').removeClass('hidden');
        $('.js-login-signup-iframe-target').addClass('hidden');
        $('.js-login-signup-iframe-target').closest('.modal--login-locker').addClass(split[1]);
        $('.js-login-signup-iframe-target').on('load', function(e){
          $('.js-login-signup-iframe-target').removeClass('hidden');
        });
      }
      else if(split[1] == 'forgot-password'){
        $('.js-login-locker-ajax-wrapper').removeClass('hidden');
        $('.js-login-signup-iframe-target').addClass('hidden');
        $('.js-login-signup-iframe-target').closest('.modal--login-locker__modal-content').addClass(split[1]);
        $('.js-login-signup-iframe-target').on('load', function(e){
          $('.js-login-signup-iframe-target').removeClass('hidden');
        });
      }
      else if (split[1] == 'is-sticky' && !$('.modal--login-locker').is(':visible')){
        $('.js-header-login-dropdown').addClass(split[1]);
      }
      else if(split[1] != 'is-sticky'){
        $('.js-login-signup-iframe-target:visible').addClass(split[1]);
        if(split[2])
         $('.modal--login-locker').addClass(split[2]);
      }
    });
  }(jQuery, window));
